// src/services/trackingService.ts
import axios from "axios";

const API_URL = "https://api.enviosuramerica.com/services-web/tracking-guia";

export const trackingGuide = async (guia: string) => {
  try {
    const response = await axios.post(
      API_URL,
      { guia }, // Aquí pasas el número de guía en el cuerpo
      {
        withCredentials: true,
        headers: {
          "Content-Type": "application/json",
        },
      }
    );

    if (response.status === 200) {
      return response.data; // Devuelve toda la respuesta
    }
  } catch (err) {
    console.error("Error al recibir la solicitud", err);
    throw err; // Lanza el error para manejarlo más arriba
  }
};
