import { type FC, useState } from "react";
import { DownloadIcon } from "lucide-react";

interface DownloadProps {
  fileName?: string;
}

export const Download: FC<DownloadProps> = ({
  fileName = "terminos-y-condiciones",
}) => {
  const [isLoading, setIsLoading] = useState(false);

  const handleDownload = async () => {
    setIsLoading(true);
    try {
      const response = await fetch(
        `${process.env.PUBLIC_URL}/documents/terminos.docx`
      );

      if (!response.ok)
        throw new Error(`HTTP error! status: ${response.status}`);

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);

      const a = document.createElement("a");
      a.href = url;
      a.download = `${fileName}.pdf`;
      document.body.appendChild(a);
      a.click();

      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      console.error("Error en descarga:", error);
      alert("Error al descargar. Contacte al soporte técnico.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <button
      onClick={handleDownload}
      disabled={isLoading}
      className="flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-all disabled:opacity-50"
    >
      <DownloadIcon size={18} />
      {isLoading ? "Generando descarga..." : "Descargar Términos"}
    </button>
  );
};
