import { type FC, useState } from "react";
import { DownloadIcon } from "lucide-react";

interface DownloadProps {
  link: string;
}

export const Download: FC<DownloadProps> = ({ link }) => {
  const [isLoading, setIsLoading] = useState(false);

  const handleDownload = async (blob: Blob, filename: string) => {
    setIsLoading(true);
    const response = await fetch(link);
    const blobs = await response.blob();

    const url = window.URL.createObjectURL(blobs);
    const a = document.createElement("a");
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    window.URL.revokeObjectURL(url);
    setIsLoading(false);
  };

  return (
    <button
      onClick={handleDownload}
      disabled={isLoading}
      className="flex items-center justify-center bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors duration-200 disabled:bg-blue-400"
    >
      {isLoading ? (
        "Descargando..."
      ) : (
        <>
          <DownloadIcon size={20} className="mr-2" />
          Descargar TÃ©rminos y Condiciones
        </>
      )}
    </button>
  );
};
