import Image from "next/image";
import { motion } from "framer-motion";

export function LetterLoading() {
  const letters = "CARGANDO".split("");

  return (
    <div className="fixed inset-0 bg-gradient-to-b from-blue-500 to-gray-800 flex flex-col items-center justify-center z-50">
      <div className="mb-8">
        <Image
          src="/logo.png" // Make sure this path is correct for your logo
          alt="Company Logo"
          width={150}
          height={150}
        />
      </div>
      <div className="text-center">
        {letters.map((letter, index) => (
          <motion.span
            key={index}
            className="text-white text-4xl font-bold inline-block"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{
              duration: 0.5,
              delay: index * 0.1,
              repeat: Infinity,
              repeatType: "reverse",
              repeatDelay: 0.5,
            }}
          >
            {letter}
          </motion.span>
        ))}
      </div>
    </div>
  );
}
