import { type FC, useState } from "react";
import { DownloadIcon } from "lucide-react";

interface DownloadProps {
  link: string;
}

export const Download: FC<DownloadProps> = ({ link }) => {
  const [isLoading, setIsLoading] = useState(false);

  const handlerDownload = () => {
    setIsLoading(true);
    const anchor = document.createElement("a");
    anchor.href = link; // Usar la ruta correcta
    anchor.setAttribute("download", "terminos.docx"); // Nombre del archivo a descargar
    document.body.appendChild(anchor);
    anchor.click();
    document.body.removeChild(anchor);
    setIsLoading(false);
  };

  return (
    <button
      onClick={() => handlerDownload}
      disabled={isLoading}
      className="flex items-center justify-center bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors duration-200 disabled:bg-blue-400"
    >
      {isLoading ? (
        "Descargando..."
      ) : (
        <>
          <DownloadIcon size={20} className="mr-2" />
          Descargar TÃ©rminos y Condiciones
        </>
      )}
    </button>
  );
};
