import { motion } from "framer-motion";
// import logosinfondo from "../assets/logosinfondo.png";

export function LetterLoading() {
  const letters = "CARGANDO".split("");

  return (
    <div className="text-center">
      {letters.map((letter, index) => (
        <motion.span
          key={index}
          className="text-white text-4xl font-bold inline-block"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{
            duration: 0.5,
            delay: index * 0.1,
            repeat: Infinity,
            repeatType: "reverse",
            repeatDelay: 0.5,
          }}
        >
          {letter}
        </motion.span>
      ))}
    </div>
  );
}
