import { type FC, useState } from "react";
import { DownloadIcon } from "lucide-react";

interface DownloadProps {
  link: string;
}

export const Download: FC<DownloadProps> = ({ link }) => {
  const [isLoading, setIsLoading] = useState(false);

  const handleDownload = async () => {
    setIsLoading(true);
    try {
      const response = await fetch(link);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const contentType = response.headers.get("content-type");
      console.log("Content-Type:", contentType);

      const blob = await response.blob();
      console.log("Blob size:", blob.size);

      if (blob.size === 0) {
        throw new Error("El archivo descargado está vacío");
      }

      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.style.display = "none";
      a.href = url;
      a.download = contentType?.includes("pdf")
        ? "terminos_y_condiciones.pdf"
        : "terminos_y_condiciones.txt";
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error("Error al descargar el archivo:", error);
      alert(`Error al descargar el archivo: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <button
      onClick={handleDownload}
      disabled={isLoading}
      className="flex items-center justify-center bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors duration-200 disabled:bg-blue-400"
    >
      {isLoading ? (
        "Descargando..."
      ) : (
        <>
          <DownloadIcon size={20} className="mr-2" />
          Descargar Términos y Condiciones
        </>
      )}
    </button>
  );
};
