// src/modules/tracking/trackings.tsx
"use client";

import React, { useState } from "react";
import { trackingGuide } from "../tracking/services/trackGuide"; // Asegúrate de que la ruta sea correcta

export function Tracking() {
  const [trackingNumber, setTrackingNumber] = useState("");
  const [trackingResult, setTrackingResult] = useState<any>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleTracking = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError(""); // Limpiar errores anteriores

    try {
      const data = await trackingGuide(trackingNumber); // Llamada a la función de seguimiento

      if (data.status === "ok") {
        setTrackingResult(data.tracking); // Guarda los datos de seguimiento
      } else {
        setTrackingResult(null); // No hay información disponible
        setError(data.mensaje); // Muestra el mensaje de error del backend
      }
    } catch (error) {
      console.error("Error:", error);
      setError("No se pudo obtener información del seguimiento.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <section
      id="tracking"
      className="py-24 bg-gradient-to-b from-white to-blue-50"
    >
      <div className="container mx-auto px-4 relative">
        <h2 className="text-5xl font-bold text-center mb-6 text-blue-600">
          Seguimiento de Envío
        </h2>
        <p className="text-gray-600 max-w-2xl mx-auto mb-12 text-center text-lg">
          Rastrea tu envío en tiempo real y mantente informado sobre su
          ubicación y estado actual.
        </p>
        <div className="max-w-xl mx-auto bg-white p-8 rounded-2xl shadow-2xl">
          <form onSubmit={handleTracking} className="space-y-6">
            <div>
              <label
                htmlFor="tracking-number"
                className="block text-sm font-medium text-gray-700 mb-1"
              >
                Número de Seguimiento
              </label>
              <input
                type="text"
                id="tracking-number"
                value={trackingNumber}
                onChange={(e) => setTrackingNumber(e.target.value)} // Actualiza el estado del número de seguimiento
                placeholder="Ingrese su número de seguimiento"
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                required
              />
            </div>
            <button
              type="submit"
              className={`w-full bg-blue-500 text-white py-3 px-4 rounded-lg hover:bg-blue-600 transition-colors text-lg font-semibold ${
                loading ? "opacity-50 cursor-not-allowed" : ""
              }`}
              disabled={loading}
            >
              {loading ? "Cargando..." : "Rastrear Envío"}
            </button>
          </form>

          {error && <p className="text-red-500">{error}</p>}
          {trackingResult && trackingResult.length > 0 && (
            <div className="mt-8 p-6 bg-blue-50 rounded-lg border border-blue-200">
              <h3 className="text-xl font-semibold text-blue-800 mb-2">
                Estado del Envío
              </h3>
              {trackingResult.map((item: any, index: number) => (
                <div key={index} className="mb-4">
                  <p>
                    <strong>Estado:</strong> {item.estado}
                  </p>
                  <p>
                    <strong>Descripción:</strong> {item.descripcion}
                  </p>
                  <p>
                    <strong>Fecha:</strong> {item.fecha}
                  </p>
                </div>
              ))}
            </div>
          )}
          {trackingResult && trackingResult.length === 0 && (
            <div className="mt-8 p-6 bg-blue-50 rounded-lg border border-blue-200">
              <h3 className="text-xl font-semibold text-blue-800 mb-2">
                Estado del Envío
              </h3>
              <p>
                No se encontraron datos para el número de seguimiento ingresado.
              </p>
            </div>
          )}
        </div>
      </div>
    </section>
  );
}
