import React, { useState } from "react";
import axios from "axios";

export type State = {
  trackingNumber: any;
  trackingResult: any;
  loading: boolean;
  error: string | null;
};

export const Tracking = () => {
  const [state, setState] = useState<State>({
    trackingNumber: [],
    trackingResult: null,
    loading: false,
    error: null,
  });

  // const handleTracking = async (e: React.FormEvent) => {
