  //       { guia: state.trackingNumber },
  //       {
  //         headers: {
  //           "Content-Type": "application/json",
  //         },
  //       }
  //     );
  //     console.log("Response from API:", response.data);

  //     if (response.status === 200) {
  //       const data = response.data;
  //       if (data.status === "ok") {
  //         console.log(data.tracking);
  //         setState((prev) => ({
  //           ...prev,
  //           trackingResult: data.tracking,
  //           loading: false,
  //         }));
  //       } else {
  //         setState((prev) => ({
  //           ...prev,
  //           trackingResult: null,
  //           error: data.mensaje || "Error desconocido",
  //           loading: false,
  //         }));
  //       }
  //     } else {
  //       throw new Error(`HTTP error status ${response.status}`);
  //     }
  //   } catch (error: any) {
  //     console.error(
  //       "Error:",
  //       error.response ? error.response.data : error.message
  //     );
  //     setState((prev) => ({
  //       ...prev,
  //       error: "No se pudo obtener información del seguimiento.",
  //       loading: false,
  //     }));
  //   }
  // };

  return (
    <section
      id="tracking"
      className="py-24 bg-gradient-to-b from-white to-blue-50"
    >
      <div className="container mx-auto px-4 relative">
        <h2 className="text-5xl font-bold text-center mb-6 text-blue-600">
          Seguimiento de Envío
        </h2>
        <p className="text-gray-600 max-w-2xl mx-auto mb-12 text-center text-lg">
          Rastrea tu envío en tiempo real y mantente informado sobre su
          ubicación y estado actual.
        </p>
        <div className="max-w-xl mx-auto bg-white p-8 rounded-2xl shadow-2xl">
          <form onSubmit={handleTracking} className="space-y-6">
            <div>
              <label
                htmlFor="tracking-number"
                className="block text-sm font-medium text-gray-700 mb-1"
              >
                Número de Seguimiento
              </label>
              <input
                type="text"
                id="tracking-number"
                value={state.trackingNumber}
                onChange={(e) =>
                  setState((prev) => ({
                    ...prev,
                    trackingNumber: e.target.value,
                  }))
                }
                placeholder="Ingrese su número de seguimiento"
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                required
              />
            </div>
            <button
              type="submit"
              className={`w-full bg-blue-500 text-white py-3 px-4 rounded-lg hover:bg-blue-600 transition-colors text-lg font-semibold ${
                state.loading ? "opacity-50 cursor-not-allowed" : ""
              }`}
              disabled={state.loading}
            >
              {state.loading ? "Cargando..." : "Rastrear Envío"}
            </button>
          </form>
          {state.error && <p className="text-red-500">{state.error}</p>}
          {state.trackingResult && state.trackingResult.length > 0 && (
            <div className="mt-8 p-6 bg-blue-50 rounded-lg border border-blue-200">
              <h3 className="text-xl font-semibold text-blue-800 mb-2">
                Estado del Envío
              </h3>
              {state.trackingResult.map((item: any, index: number) => (
                <div key={index} className="mb-4">
                  <p>
                    <strong>Estado:</strong> {item.estado}
                  </p>
                  <p>
                    <strong>Descripción:</strong> {item.descripcion}
                  </p>
                  <p>
                    <strong>Fecha:</strong> {item.fecha}
                  </p>
                </div>
              ))}
            </div>
          )}
          {state.trackingResult && state.trackingResult.length === 0 && (
            <div className="mt-8 p-6 bg-blue-50 rounded-lg border border-blue-200">
              <h3 className="text-xl font-semibold text-blue-800 mb-2">
                Estado del Envío
              </h3>
              <p>
                No se encontraron datos para el número de seguimiento ingresado.
              </p>
            </div>
          )}
        </div>
      </div>
    </section>
  );
};
