import { useState } from "react";
import logotipo from "../../assets/logotipo.jpeg";

type Images = { src: string; alt: string; title: string; description: string };
export function FeaturedImages() {
  const [hoveredImage, setHoveredImage] = useState<number | null>(null);

  const images: Images[] = [
    {
      src: logotipo,
      alt: "Logística Global",
      title: "Logística Global",
      description:
        "Soluciones de transporte internacional eficientes y confiables.",
    },
    {
      src: "/placeholder.svg?height=600&width=800",
      alt: "Tecnología Avanzada",
      title: "Tecnología Avanzada",
      description: "Sistemas de seguimiento y gestión de última generación.",
    },
  ];

  return (
    <section className="py-24 bg-gradient-to-b from-blue-50 to-white">
      <div className="container mx-auto px-[100vw]">
        <h2 className="text-4xl md:text-5xl font-bold text-center mb-12 text-blue-800">
          Nuestro Enfoque
        </h2>
        <div className="grid md:grid-cols-1 gap-8">
          {images.map((image, index) => (
            <div
              key={index}
              className="relative overflow-hidden rounded-xl shadow-lg transition-all duration-300 hover:shadow-2xl"
              onMouseEnter={() => setHoveredImage(index)}
              onMouseLeave={() => setHoveredImage(null)}
            >
              <img
                src={image.src}
                alt={image.alt}
                width={800}
                height={600}
                className="w-full h-auto transition-transform duration-300 transform hover:scale-105"
              />
              <div
                className={`absolute inset-0 bg-gradient-to-t from-black/70 to-transparent flex items-end p-6 transition-opacity duration-300 ${
                  hoveredImage === index ? "opacity-100" : "opacity-0"
                }`}
              >
                <div>
                  <h3 className="text-2xl font-bold text-white mb-2">
                    {image.title}
                  </h3>
                  <p className="text-white text-sm">{image.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
