import { X } from "lucide-react";
import type React from "react";
import { useState, useEffect } from "react";
import { Download } from "../dowloadArchive";

interface ModalProps {
  isOpen: boolean;
  onClose: () => void;
  children?: React.ReactNode;
  pdfUrl?: string;
}

export const TermsModal: React.FC<ModalProps> = ({
  isOpen,
  onClose,
  children,
  pdfUrl,
}) => {
  const fileLink = "../../assets/terminos2.pdf";
  const [pdfBlob, setPdfBlob] = useState<Blob | null>(null);

  useEffect(() => {
    if (!pdfUrl) {
      console.error("No se proporcionó una URL para el PDF.");
      return;
    }

    const fetchPdf = async () => {
      try {
        const response = await fetch(pdfUrl);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const blob = await response.blob();
        setPdfBlob(blob);
      } catch (error) {
        console.error("Error fetching PDF:", error);
        alert("Error al cargar el PDF. Por favor, inténtelo de nuevo.");
      }
    };
    if (isOpen) {
      fetchPdf();
    }
  }, [fileLink, pdfUrl, isOpen]);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div
        className="fixed inset-0 bg-black bg-opacity-50 transition-opacity"
        onClick={onClose}
      ></div>
      <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-screen-xl mx-4 max-h-[70vh] overflow-y-auto z-10 relative">
        <button
          onClick={onClose}
          className="absolute top-4 right-4 text-gray-500 hover:text-gray-700 transition-colors duration-200"
          aria-label="Cerrar"
        >
          <X size={24} />
        </button>
        <h2 className="text-2xl font-bold mb-4 text-blue-500">
          Términos y Condiciones
        </h2>
        <div className="text-gray-600 space-y-4">
          {children || (
            <>
              <p>
                Bienvenido a nuestros Términos y Condiciones. Por favor, lea
                cuidadosamente antes de usar nuestros servicios.
              </p>
              <h3 className="text-lg font-semibold text-gray-700">
                1. Aceptación de los Términos
              </h3>
              <p>
                Al acceder o utilizar nuestro servicio, usted acepta estar
                sujeto a estos Términos y Condiciones.
              </p>
              <h3 className="text-lg font-semibold text-gray-700">
                2. Cambios en los Términos
              </h3>
              <p>
                Nos reservamos el derecho de modificar o reemplazar estos
                Términos en cualquier momento.
              </p>
            </>
          )}
        </div>
        <div className="mt-6 flex flex-col sm:flex-row justify-between items-center">
          {pdfBlob && <Download link="" blob={pdfBlob} />}
          <button
            onClick={onClose}
            className="mb-2 sm:mb-0 sm:mr-2 px-4 py-2 bg-gray-400 text-white rounded hover:bg-blue-700 transition-colors duration-200"
          >
            Cerrar
          </button>
        </div>
      </div>
    </div>
  );
};
